import ReactDOM from 'react-dom';
import React from 'react';
import './index.css';

class Board extends React.Component {
    handlerBoxClick(i){
        this.props.handlerForBoxClick(i);
    }
    renderSquare(i){
        return(
            <button onClick={()=>this.handlerBoxClick(i)}>{this.props.boxes[i] ==null? "":this.props.boxes[i]}</button>
        );
    }
    render() {
        return (
            <div className='board'>
                <div className='title'>
                    Tic Tac Toe
                </div>
                <div className='content'>
                    <div className="ttt">
                        <div className='row'>
                            {this.renderSquare(0)}
                            {this.renderSquare(1)}
                            {this.renderSquare(2)}
                        </div>
                        <div className='row'>
                            {this.renderSquare(3)}
                            {this.renderSquare(4)}
                            {this.renderSquare(5)}                        
                        </div>
                        <div className='row'>
                            {this.renderSquare(6)}
                            {this.renderSquare(7)}
                            {this.renderSquare(8)}                        
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

class Display extends React.Component {
    render() {
        let gameStatus = 'Next move for "X"';

        return (
            <div className='display'>
                <div className='title'>
                    {gameStatus}
                </div>
                <div className='content'>
                    <div className='history'>
                        <button>Go to start</button>

                    </div>
                </div>
            </div>
        );
    }
}

class TTT extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            history:[
                [null,null,null,null,null,null,null,null,null]
            ],
            stepNumber: 0,
            gameStatus:null
        }
    }
    handlerSquareClick(i){
        alert(i);
    }
    render() {
        let squares =this.state.history[this.state.history.length-1];
        return (
            <>
                <Board handlerForBoxClick ={(i)=>this.handlerSquareClick(i)} boxes = {squares}/>
                <Display />
            </>
        );
    }
}

ReactDOM.render(<TTT />, document.getElementById("root"))
